import React,{useState} from 'react';
import './App.css';
import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom';
import JournalPage from './page/JournalPage';
import HomePage from './page/HomePage';


function App() {
    const [menuHidden, setMenuHidden] = useState(false);
  return (
    <Router>
        <div className="app-container d-flex">

            <div className={`sidebar bg-orange ${menuHidden ? 'hidden' : ''}`}>
                <button className="btn btn-primary toggle-button"
                        onClick={() => setMenuHidden(!menuHidden)}>
                        {menuHidden ? '>>' : '<<'}
                </button>
                <nav className="nav flex-column mt-5" >
                    {/* <ul class="nav flex-column mt-5">
                        <li class="nav-item">
                            <a class="nav-link" href="/">Home</a>
                        </li>
                        <li class="nav-item">
                            <a class="nav-link" href="/journal">urnal Umum</a>
                        </li>
                    </ul> */}
                    <Link className="nav-link active" to="/">Home</Link>
                    <Link className="nav-link" to="/journal">Jurnal Umum</Link>
                </nav>
            </div>
            <div className={`content flex-grow-1 p-3 ${menuHidden ? 'content-expanded' : ''}`}>
                    <h1 className="text-center mb-4">Aplikasi Jurnal Umum</h1>
                    <Routes>
                        <Route path="/" element={<HomePage />} />
                        <Route path="/journal" element={<JournalPage />} />
                    </Routes>
            </div>
        </div>
    </Router>
  );
}

export default App;
