import React, { useState, useEffect } from 'react';
import { Table, Pagination } from 'react-bootstrap';
import moment from "moment";

function NeracaSaldoPage() {
    const [Trialbalance, setTrialbalance] = useState([]);
    const [loading, setLoading] = useState(true);
    const [currentPage, setCurrentPage] = useState(1);
    const [totalPages, setTotalPages] = useState(1);

    useEffect(() => {
        setLoading(true);
        fetch(`http://localhost:7000/api/transactions/trial-balance`)
            .then((res) => res.json())
            .then((data) => {
                data.data.forEach(result => {
                    result.date = moment(new Date(result.date)).utc().format("DD/MM/YYYY");
                    const amountTmp = new Intl.NumberFormat("id-ID", {
                        style: "currency",
                        currency: "IDR",
                        maximumFractionDigits: 0,
                        }).format(result.amount);
                    result.amount = amountTmp.replace("Rp", "");
                    }); 

                setTrialbalance(data.data);
                setTotalPages(data.totalPages);
                setLoading(false);
            });
    },  [currentPage]);

    const handlePageChange = (page) => {
        setCurrentPage(page);
    };

    return (
        <div>
            <div className="d-flex justify-content-between align-items-center mb-4">
            <h2>Neraca Saldo</h2>
            </div>
                {loading ? (
                        <div className="text-center">Loading...</div>
                    ) : (
                            <Table striped bordered hover>
                                <thead>
                                    <tr>
                                        <th>No</th>
                                        <th>Ref</th>
                                        <th>Akun</th>
                                        <th>Debit</th>
                                        <th>Credit</th>
                                    </tr>
                                </thead>
                                <tbody>
                                {Trialbalance.map((trialBalance, index) => (
                                    <tr key={index}>
                                        <td>{index + 1}</td>
                                        <td>{trialBalance.ref}</td>
                                        <td>{trialBalance.name}</td>
                                        <td>{trialBalance.debit}</td>
                                        <td>{trialBalance.kredit}</td>

                                    </tr>
                                ))}
                                </tbody>
                            </Table>
                    )}
                   <Pagination>
                                       <Pagination.Prev
                                               disabled={currentPage === 1}
                                               onClick={() => handlePageChange(currentPage - 1)}
                                           />
                                           {currentPage > 3 && (
                                               <>
                                                   <Pagination.Item onClick={() => handlePageChange(1)}>1</Pagination.Item>
                                                   <Pagination.Ellipsis />
                                               </>
                                           )}
                                           {[...Array(totalPages).keys()].map((page) => (
                                               <Pagination.Item
                                                   key={page + 1}
                                                   active={page + 1 === currentPage}
                                                   onClick={() => handlePageChange(page + 1)}
                                               >
                                                   {page + 1}
                                               </Pagination.Item>
                                           ))}
                                           {currentPage < totalPages - 2 && (
                                               <>
                                                   <Pagination.Ellipsis />
                                                   <Pagination.Item onClick={() => handlePageChange(totalPages)}>
                                                       {totalPages}
                                                   </Pagination.Item>
                                               </>
                                           )}
                                           <Pagination.Next
                                               disabled={currentPage === totalPages}
                                               onClick={() => handlePageChange(currentPage + 1)}
                                           />
                                       </Pagination>
                </div>
    );
}

export default NeracaSaldoPage;
