import React, { useState, useEffect } from 'react';
import { Table, Pagination } from 'react-bootstrap';
import moment from "moment";

function PenyesuaianPage() {
    const [Penyesuaian, setPenyesuaian] = useState([]);
    const [loading, setLoading] = useState(true);
    const [currentPage, setCurrentPage] = useState(1);
    const [totalPages, setTotalPages] = useState(1);


    useEffect(() => {
        setLoading(true);
        fetch(`http://localhost:7000/api/transactions/adjustments`)
            .then((res) => res.json())
            .then((data) => {
                data.data.forEach(result => {
                    result.date = moment(new Date(result.date)).utc().format("DD/MM/YYYY");
                    var debit = result.debit;
                    var kredit = result.kredit;

                    const amountDebit = new Intl.NumberFormat("id-ID", {
                        style: "currency",
                        currency: "IDR",
                        maximumFractionDigits: 0,
                        }).format(debit);
                    const amountKredit = new Intl.NumberFormat("id-ID", {
                        style: "currency",
                        currency: "IDR",
                        maximumFractionDigits: 0,
                        }).format(kredit);        
                    result.debit = amountDebit.replace("Rp", "");
                    result.kredit = amountKredit.replace("Rp", "");


                    }); 

                setPenyesuaian(data.data);
                setTotalPages(data.totalPages);
                setLoading(false);
            });
    },  [currentPage]);

    const handlePageChange = (page) => {
        setCurrentPage(page);
    };

    return (
        <div>
            <div className="d-flex justify-content-between align-items-center mb-4">
            <h2>Penyesuaian</h2>
            </div>
                {loading ? (
                        <div className="text-center">Loading...</div>
                    ) : (
                        <Table striped bordered hover>
                            <thead>
                                <tr>
                                    <th>No</th>
                                    <th>Ref</th>
                                    <th>Akun</th>
                                    <th>Debit</th>
                                    <th>Credit</th>
                                </tr>
                            </thead>
                            <tbody>
                            {Penyesuaian.map((penyesuaian, index) => (
                                <tr key={index}>
                                    <td>{index + 1}</td>
                                    <td>{penyesuaian.code}</td>
                                    <td>{penyesuaian.name}</td>
                                    <td>{penyesuaian.debit}</td>
                                    <td>{penyesuaian.kredit}</td>
                                </tr>
                            ))}
                            </tbody>
                        </Table>     
                    )}
                   <Pagination>
                                       <Pagination.Prev
                                               disabled={currentPage === 1}
                                               onClick={() => handlePageChange(currentPage - 1)}
                                           />
                                           {currentPage > 3 && (
                                               <>
                                                   <Pagination.Item onClick={() => handlePageChange(1)}>1</Pagination.Item>
                                                   <Pagination.Ellipsis />
                                               </>
                                           )}
                                           {[...Array(totalPages).keys()].map((page) => (
                                               <Pagination.Item
                                                   key={page + 1}
                                                   active={page + 1 === currentPage}
                                                   onClick={() => handlePageChange(page + 1)}
                                               >
                                                   {page + 1}
                                               </Pagination.Item>
                                           ))}
                                           {currentPage < totalPages - 2 && (
                                               <>
                                                   <Pagination.Ellipsis />
                                                   <Pagination.Item onClick={() => handlePageChange(totalPages)}>
                                                       {totalPages}
                                                   </Pagination.Item>
                                               </>
                                           )}
                                           <Pagination.Next
                                               disabled={currentPage === totalPages}
                                               onClick={() => handlePageChange(currentPage + 1)}
                                           />
                                       </Pagination>
                </div>
    );
}

export default PenyesuaianPage;
