import React, { useState, useEffect } from 'react';
import { Table } from 'react-bootstrap';
import moment from "moment";

function ModalPage() {    
    const [Modal, setModal] = useState([]);
    const [loading, setLoading] = useState(true);


    useEffect(() => {
        setLoading(true);
        fetch(`http://localhost:7000/api/transactions/modal`)
            .then((res) => res.json())
            .then((data) => {
                data.data.forEach(result => {
                    result.date = moment(new Date(result.date)).utc().format("DD/MM/YYYY");
                    var debit = result.debit;
                    var kredit = result.kredit;

                    const amountDebit = new Intl.NumberFormat("id-ID", {
                        style: "currency",
                        currency: "IDR",
                        maximumFractionDigits: 0,
                        }).format(debit);
                    const amountKredit = new Intl.NumberFormat("id-ID", {
                        style: "currency",
                        currency: "IDR",
                        maximumFractionDigits: 0,
                        }).format(kredit);        
                    result.debit = amountDebit.replace("Rp", "");
                    result.kredit = amountKredit.replace("Rp", "");


                    }); 

                    setModal(data.data);
                    setLoading(false);
            });
    },  []);

    return (
        <div>
            <div className="d-flex justify-content-between align-items-center mb-4">
            <h2>Perubahan Modal</h2>
            </div>
                {loading ? (
                        <div className="text-center">Loading...</div>
                    ) : (
                        <Table striped bordered hover>
                            <thead>
                                <tr>
                                    <th>No</th>
                                    <th>Ref</th>
                                    <th>Akun</th>
                                    <th>Debit</th>
                                    <th>Credit</th>
                                </tr>
                            </thead>
                            <tbody>
                            {Modal.map((modal, index) => (
                                <tr key={index}>
                                    <td>{index + 1}</td>
                                    <td>{modal.code}</td>
                                    <td>{modal.name}</td>
                                    <td>{modal.debit}</td>
                                    <td>{modal.kredit}</td>
                                </tr>
                            ))}
                            </tbody>
                        </Table>     
                    )}
                </div>
    );
}

export default ModalPage;
