import React,{useState} from 'react';
import './App.css';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import HomePage from './page/HomePage';
import BukuBesarPage from './page/BukuBesarPage';
import AkunPage from './page/AkunPage';


function App() {
    const [menuHidden, setMenuHidden] = useState(false);
  return (
    <Router>
        <div className="app-container d-flex">

            <div className={`sidebar bg-orange ${menuHidden ? 'hidden' : ''}`}>
                <button className="btn btn-primarys toggle-button"
                        onClick={() => setMenuHidden(!menuHidden)}>
                        {menuHidden ? '>>' : '<<'}
                </button>
                <nav className="nav" >
                    <ul class="nav flex-column mt-5">
                        <li class="nav-item">
                            <a class="nav-link" href="/">Home</a>
                        </li>
                        <li class="nav-item">
                            <a class="nav-link" href="/akun">Akun</a>
                        </li>
                    </ul>
                </nav>
            </div>
            <div className={`content flex-grow-1 p-3 ${menuHidden ? 'content-expanded' : ''}`}>
                    <h1 className="text-center mb-4">Aplikasi Jurnal Umum</h1>
                    <Routes>
                        <Route path="/" element={<HomePage />} />
                        <Route path="/journal" element={<AkunPage />} />
                    </Routes>
            </div>
        </div>
    </Router>
  );
}

export default App;
