import React, { useState, useEffect } from 'react';
import { Pagination } from 'react-bootstrap';
import axios from 'axios';

function AkunPage() {
    const [journals, setJournals] = useState([]);
    const [loading, setLoading] = useState(true);
    const [currentPage, setCurrentPage] = useState(1);
    const [totalPages, setTotalPages] = useState(1);
    const [formData, setFormData] = useState({
        code: '',
        name: '',
        type: '',
    });
    const [errors, setErrors] = useState({});
    const [showForm, setShowForm] = useState(false);

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });
    };
    const validateForm = () => {
        const newErrors = {};
        if (!formData.akun || !/^[0-9 ]+$/.test(formData.akun)) {
            newErrors.akun = 'Akun must be a valid number.';
        }
        if (!formData.nama || !/^[a-zA-Z ]+$/.test(formData.nama)) {
            newErrors.nama = 'Nama must be a valid character.';
        }
        if (!formData.type || !/^[a-zA-Z ]+$/.test(formData.type)) {
            newErrors.type = 'Type must be a valid character.';
        }
        setErrors(newErrors);
        return Object.keys(newErrors).length === 0;
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (validateForm()) {
            try {
                const response = await axios.post('http://localhost:7000/api/accounts/send', formData);
                setJournals([...journals, response.data]);
                setFormData({ code: '', name: '', type: '' });
                setShowForm(false);
            } catch (error) {
                console.error('Error adding journal:', error);
            }
        }
    };

    useEffect(() => {
        setLoading(true);
        fetch(`http://localhost:7000/api/accounts/list?page=${currentPage}`)
            .then((res) => res.json())
            .then((data) => {
                setJournals(data.data);
                setTotalPages(data.totalPages);
                setLoading(false);
            });
    }, [currentPage]);

    const handlePageChange = (page) => {
        setCurrentPage(page);
    };

    return (
        <div>
            <div className="d-flex justify-content-between align-items-center mb-4">
                <h2>Data Jurnal Umum</h2>
                <button
                    className="btn btn-primary"
                    onClick={() => setShowForm(!showForm)}
                >
                    {showForm ? 'Tutup Form' : 'Tambah Akun'}
                </button>
            </div>

            {showForm ? (
                <div className="card mb-4">
                    <div className="card-header">Input Jurnal Umum</div>
                    <div className="card-body">
                        <form onSubmit={handleSubmit}>
                            <div className="mb-3">
                                <label className="form-label">Akun</label>
                                <input
                                    type="text"
                                    className={`form-control ${errors.akun ? 'is-invalid' : ''}`}
                                    name="code"
                                    value={formData.code}
                                    onChange={handleInputChange}
                                    required
                                />
                                {errors.akun && <div className="invalid-feedback">{errors.akun}</div>}
                            </div>
                            <div className="mb-3">
                                <label className="form-label">Nama</label>
                                <input
                                    type="text"
                                    className={`form-control ${errors.nama ? 'is-invalid' : ''}`}
                                    name="name"
                                    value={formData.name}
                                    onChange={handleInputChange}
                                    required
                                />
                                {errors.nama && <div className="invalid-feedback">{errors.nama}</div>}
                            </div>
                            <div className="mb-3">
                                <label className="form-label">Type</label>
                                <input
                                    type="text"
                                    className={`form-control ${errors.type ? 'is-invalid' : ''}`}
                                    name="type"
                                    value={formData.type}
                                    onChange={handleInputChange}
                                    required
                                />
                                {errors.type && <div className="invalid-feedback">{errors.type}</div>}
                            </div>
                            <button type="submit" className="btn btn-success">
                                Simpan
                            </button>
                        </form>
                    </div>
                </div>
            ):(
            <div className="card">
                <div className="card-header">Data Jurnal Umum</div>
                <div className="card-body">
                {loading ? (
                        <div className="text-center">Loading...</div>
                    ) : (
                    <table className="table table-striped">
                        <thead>
                            <tr>
                                <th>No</th>
                                <th>Akun</th>
                                <th>Nama</th>
                                <th>Type</th>
                            </tr>
                        </thead>
                        <tbody>
                            {journals.map((journal,index) => (
                                <tr key={index}>
                                    <td>{index + 1}</td>
                                    <td>{journal.code}</td>
                                    <td>{journal.name}</td>
                                    <td>{journal.type}</td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                    )}
                    <Pagination>
                        {[...Array(totalPages).keys()].map((page) => (
                            <Pagination.Item
                                key={page + 1}
                                active={page + 1 === currentPage}
                                onClick={() => handlePageChange(page + 1)}
                            >
                                {page + 1}
                            </Pagination.Item>
                        ))}
                    </Pagination>
                </div>
            </div>
            )}
        </div>
    );
}

export default JournalPage;
