import React, { useState, useEffect } from 'react';
import { Pagination } from 'react-bootstrap';


function BukuBesarPage() {
    const [ledgers, setLedgers] = useState([]);
    const [loading, setLoading] = useState(true);
    const [currentPage, setCurrentPage] = useState(1);
    const [totalPages, setTotalPages] = useState(1);
    const [accounts, setAccounts] = useState([]);
    const [selectedAccount, setSelectedAccount] = useState(101);
    useEffect(() => {
        // Fetch list of accounts
        fetch('http://localhost:7000/api/accounts')
            .then((res) => res.json())
            .then((data) => {
                setAccounts(data);
                setSelectedAccount(data[0]?.code || 101);
            });
    }, []);

    useEffect(() => {
        setLoading(true);
        fetch(`http://localhost:7000/api/transactions//ledger/${selectedAccount}?page=${currentPage}`)
            .then((res) => res.json())
            .then((data) => {
                console.log(data);
                setLedgers(data.data);
                setTotalPages(data.totalPages);
                setLoading(false);
            });
    },  [selectedAccount, currentPage]);

    const handleAccountChange = (event) => {
        setSelectedAccount(event.target.value);
        setCurrentPage(1); // Reset to first page
    };

    const handlePageChange = (page) => {
        setCurrentPage(page);
    };

    return (
        <div>
            <div className="card">
                <div className="card-header">Buku Besar</div>
                <div className="card-body">
                    <div className="mb-3">
                        <label htmlFor="accountSelect" className="form-label">Pilih Akun:</label>
                        <select
                            id="accountSelect"
                            className="form-select"
                            value={selectedAccount}
                            onChange={handleAccountChange}>
                            {accounts.map((account) => (
                                <option key={account.code} value={account.code}>
                                    {account.name}
                                </option>
                            ))}
                        </select>
                    </div>     
                {loading ? (
                        <div className="text-center">Loading...</div>
                    ) : (
                    <table className="table table-striped">
                        <thead>
                            <tr>
                                <th>No</th>
                                <th>Tanggal</th>
                                <th>Akun</th>
                                <th>Debit</th>
                                <th>Credit</th>
                            </tr>
                        </thead>
                        <tbody>
                        {ledgers.map((ledger, index) => (
                            <tr key={index}>
                                <td>{index + 1}</td>
                                <td>{ledger.date}</td>
                                <td>{ledger.account}</td>
                                { ledger.type === 'debit' ?(
                                    <td>{ledger.amount}</td>
                                ):(   
                                    <td>{ledger.amount}</td>
                                )}  
                            </tr>
                        ))}
                        </tbody>
                    </table>
                    )}
                    <Pagination>
                        {[...Array(totalPages).keys()].map((page) => (
                            <Pagination.Item
                                key={page + 1}
                                active={page + 1 === currentPage}
                                onClick={() => handlePageChange(page + 1)}
                            >
                                {page + 1}
                            </Pagination.Item>
                        ))}
                    </Pagination>
                </div>
            </div>
        </div>
    );
}

export default BukuBesarPage;
