import React, { useState, useEffect } from 'react';
import { Table, Pagination } from 'react-bootstrap';
import moment from "moment";

function NeracaSaldoPage() {
    const [Trialbalance, setTrialbalance] = useState([]);
    const [loading, setLoading] = useState(true);
    const [currentPage, setCurrentPage] = useState(1);
    const [totalPages, setTotalPages] = useState(1);
    const [accounts, setAccounts] = useState([]);
       const [selectedAccount, setSelectedAccount] = useState(101);
       useEffect(() => {
           // Fetch list of accounts
           fetch('http://localhost:7000/api/accounts')
               .then((res) => res.json())
               .then((data) => {
                   setAccounts(data.data);
                   setSelectedAccount(data.data[0]?.code || 101);
               });
       }, []);
    useEffect(() => {
        setLoading(true);
        fetch(`http://localhost:7000/api/transactions/trial-balance/${selectedAccount}`)
            .then((res) => res.json())
            .then((data) => {
                data.data.forEach(result => {
                    result.date = moment(new Date(result.date)).utc().format("DD/MM/YYYY");
                    var debit = result.debit;
                    var kredit = result.kredit;
                    if(debit !== 0 && kredit !==0){
                        if(debit > kredit) {
                            debit = debit - kredit;
                            kredit =0;
                        }
                    }
                    if(debit === 0 && kredit !==0){
                        debit = 0;
                    }

                    const amountDebit = new Intl.NumberFormat("id-ID", {
                        style: "currency",
                        currency: "IDR",
                        maximumFractionDigits: 0,
                        }).format(debit);
                    const amountKredit = new Intl.NumberFormat("id-ID", {
                        style: "currency",
                        currency: "IDR",
                        maximumFractionDigits: 0,
                        }).format(kredit);        
                    result.debit = amountDebit.replace("Rp", "");
                    result.kredit = amountKredit.replace("Rp", "");


                    }); 

                setTrialbalance(data.data);
                setTotalPages(data.totalPages);
                setLoading(false);
            });
    },  [selectedAccount,currentPage]);
    const handleAccountChange = (event) => {
        setSelectedAccount(event.target.value);
        setCurrentPage(1); // Reset to first page
    };
    const handlePageChange = (page) => {
        setCurrentPage(page);
    };

    return (
        <div>
            <div className="d-flex justify-content-between align-items-center mb-4">
            <h2>Neraca Saldo</h2>
            </div>
                {loading ? (
                        <div className="text-center">Loading...</div>
                    ) : (
                        <div>
                                <div className='d-flex align-items-end flex-column mb-3'>
                                    <div className='p-2'>
                                        <div className='input-group'>     
                                            <span className='input-group-text'>Akun</span> 
                                                <select
                                                    id="accountSelect"
                                                    className="form-select"
                                                    value={selectedAccount}
                                                    onChange={handleAccountChange}>
                                                    {accounts.map((account,index) => (
                                                        <option key={index + 1} value={account.code}>
                                                            {account.code +" - "+ account.name}
                                                        </option>
                                                    ))}
                                                </select></div>
                                        </div>
                                    </div>
                            <div><br/></div>   
                            <Table striped bordered hover>
                                <thead>
                                    <tr>
                                        <th>No</th>
                                        <th>Ref</th>
                                        <th>Akun</th>
                                        <th>Debit</th>
                                        <th>Credit</th>
                                    </tr>
                                </thead>
                                <tbody>
                                {Trialbalance.map((trialBalance, index) => (
                                    <tr key={index}>
                                        <td>{index + 1}</td>
                                        <td>{trialBalance.code}</td>
                                        <td>{trialBalance.name}</td>
                                        <td>{trialBalance.debit}</td>
                                        <td>{trialBalance.kredit}</td>
                                    </tr>
                                ))}
                                </tbody>
                            </Table>
                      </div>      
                    )}
                   <Pagination>
                                       <Pagination.Prev
                                               disabled={currentPage === 1}
                                               onClick={() => handlePageChange(currentPage - 1)}
                                           />
                                           {currentPage > 3 && (
                                               <>
                                                   <Pagination.Item onClick={() => handlePageChange(1)}>1</Pagination.Item>
                                                   <Pagination.Ellipsis />
                                               </>
                                           )}
                                           {[...Array(totalPages).keys()].map((page) => (
                                               <Pagination.Item
                                                   key={page + 1}
                                                   active={page + 1 === currentPage}
                                                   onClick={() => handlePageChange(page + 1)}
                                               >
                                                   {page + 1}
                                               </Pagination.Item>
                                           ))}
                                           {currentPage < totalPages - 2 && (
                                               <>
                                                   <Pagination.Ellipsis />
                                                   <Pagination.Item onClick={() => handlePageChange(totalPages)}>
                                                       {totalPages}
                                                   </Pagination.Item>
                                               </>
                                           )}
                                           <Pagination.Next
                                               disabled={currentPage === totalPages}
                                               onClick={() => handlePageChange(currentPage + 1)}
                                           />
                                       </Pagination>
                </div>
    );
}

export default NeracaSaldoPage;
