// src/pages/JournalPage.js
import React, { useState, useEffect } from 'react';
import { Button, Table, Form, Modal, Pagination } from 'react-bootstrap';
import moment from "moment";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import { FaCalendarAlt } from 'react-icons/fa';

function CustomInput({value,onClick}){
    return(
        <div className='input-group'>
            <input className='form-control' 
            placeholder='dd/mm/yyyy' type='text' 
            value={formData.date}
            onChange={handleInputChange}
            isInvalid={!!errors.date}
            onClick={onClick} readOnly/>
            <div className='input-group-append'>
                <span className='input-group-text'><FaCalendarAlt/></span>
            </div>
        </div>
    );
}
function JournalPage() {
    const [journals, setJournals] = useState([]);
    const [loading, setLoading] = useState(true);
    const [showModal, setShowModal] = useState(false);
    const [formData, setFormData] = useState({ account_id: '', type: '', amount: '' , date: '' });
    const [errors, setErrors] = useState({});
    const [currentPage, setCurrentPage] = useState(1);
    const [totalPages, setTotalPages] = useState(1);
    const [isClicked, setIsCliked] = useState(false);
    const [startDate, setStartDate] = useState(null);



    const fetchData = (page) => {
        setLoading(true);
        fetch(`http://localhost:7000/api/transactions?page=${page}`)
            .then((response) => response.json())
            .then((data) => {
                const formattedData = data.journals.map(journal => ({
                    ...journal,
                    date: new Date(journal.date).toLocaleDateString('id-ID') // Format dd/mm/yyyy
                }));
                setJournals(formattedData);
                setJournals(data.data);
                setTotalPages(data.totalPages);
                setLoading(false);
            })
            .catch((error) => {
                console.error('Error fetching data:', error);
                setLoading(false);
            });
    };

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        if (name === 'amount') {
            const formattedValue = value
                .replace(/\D/g, '') // Remove non-numeric characters
                .replace(/\B(?=(\d{3})+(?!\d))/g, '.'); // Add commas for thousands
            setFormData({ ...formData, [name]: formattedValue });
        } else {
            setFormData({ ...formData, [name]: value });
        }
    };

    const validateDate = (date) => {
        const dateRegex = /^\d{4}-\d{2}-\d{2}$/;
        if (!dateRegex.test(date)) return 'Format date must be YYYY-MM-DD';
        const inputDate = new Date(date);
        const currentDate = new Date();
        if (inputDate !== currentDate) return "The date is not the same as today's date";
        return null;
    };

    const validateForm = () => {
        const newErrors = {};
        if (!formData.account_id.match(/^[0-9]+$/)) newErrors.description = 'Akun must be a valid number.';
        if (!formData.type.match(/^[a-zA-Z ]+$/)) newErrors.debit = 'Type must be a valid character.';
        if (!formData.amount.match(/^[0-9 ]+$/)) newErrors.credit = 'amount must be a valid number.';
        const dateTmp = moment(formData.date).format("YYYY-MM-DD");
        const dateError = validateDate(dateTmp);
        if (dateError) newErrors.date = dateError;
        setErrors(newErrors);
        return Object.keys(newErrors).length === 0;
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        if (!validateForm()) return;
        console.log(formData);
        // fetch('http://localhost:7000/api/transactions/send', {
        //     method: 'POST',
        //     headers: { 'Content-Type': 'application/json' },
        //     body: JSON.stringify(formData),
        // })
        //     .then((response) => response.json())
        //     .then((data) => {
        //         setFormData({ account_id: '', type: '', amount: '', date: '' });
        //         setShowModal(false);
        //         setIsCliked(bool => !bool);
        //     })
        //     .catch((error) => console.error('Error submitting data:', error));
    };

    const handlePageChange = (page) => {
        setCurrentPage(page);
    };



    useEffect(() => {
        fetchData(currentPage);
    }, [currentPage,isClicked]);
    return (
        <div>
            <div className="d-flex justify-content-between align-items-center mb-4">
                <h2>Jurnal Umum</h2>
                <Button className="mb-3" onClick={() => setShowModal(true)}>
                    Tambah Jurnal
                </Button>
            </div>
            <div className="card">
                <div className="card-header">Data Jurnal Umum</div>
                <div className="card-body">
                    {loading ? (
                        <div>Loading...</div>
                    ) : (
                        <Table striped bordered hover>
                            <thead>
                                <tr>
                                    <th>No</th>
                                    <th>Tanggal</th>
                                    <th>Akun</th>
                                    <th>Debit</th>
                                    <th>Kredit</th>

                                </tr>
                            </thead>
                            <tbody>
                                {journals.map((journal, index) => (
                                    <tr key={index}>
                                        <td>{index + 1}</td>
                                        <td>{journal.date}</td>
                                        <td>{journal.account_id}</td>
                                        { journal.type === 'debit' ?(
                                            <td>{journal.amount}</td>
                                        ):(   
                                            <td>{journal.amount}</td>
                                        )}  
                                    </tr>
                                ))}
                            </tbody>
                        </Table>
                    )}
                    <Pagination>
                        {[...Array(totalPages).keys()].map((page) => (
                            <Pagination.Item
                                key={page + 1}
                                active={page + 1 === currentPage}
                                onClick={() => handlePageChange(page + 1)}
                            >
                                {page + 1}
                            </Pagination.Item>
                        ))}
                    </Pagination>
                </div>
            </div>
            <Modal show={showModal} onHide={() => setShowModal(false)}>
                <Modal.Header closeButton>
                    <Modal.Title>Tambah Jurnal</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <Form onSubmit={handleSubmit}>

                    <Form.Group className="mb-3">
                            <Form.Label>Tanggal</Form.Label>
                            {/* <Form.Control
                                type="date"
                                name="date"
                                value={formData.date}
                                onChange={handleInputChange}
                                isInvalid={!!errors.date}
                            /> */}
                            <label>  
                        <DatePicker className='form-control' 
                    selected={startDate} onChange={(date) => setStartDate(date)} dateFormat='dd/MM/YYYY'
                    customInput={<CustomInput/>} /> </label>
                            <Form.Control.Feedback type="invalid">
                                {errors.date}
                            </Form.Control.Feedback>
                        </Form.Group>

                        <Form.Group className="mb-3">
                            <Form.Label>Akun</Form.Label>
                            <Form.Control
                                type="text"
                                name="account_id"
                                value={formData.account_id}
                                onChange={handleInputChange}
                                isInvalid={!!errors.account_id}
                            />
                            <Form.Control.Feedback type="invalid">
                                {errors.account_id}
                            </Form.Control.Feedback>
                        </Form.Group>

                        <Form.Group className="mb-3">
                            <Form.Label>Type</Form.Label>
                            <Form.Select
                                name="type"
                                value={formData.type}
                                onChange={handleInputChange}
                                isInvalid={!!errors.type}>
                                <option value="">Pilih Type</option>
                                <option value="Debit">Debit</option>
                                <option value="Kredit">Kredit</option>
                            </Form.Select>
                            <Form.Control.Feedback type="invalid">
                                {errors.type}
                            </Form.Control.Feedback>
                        </Form.Group>

                        <Form.Group className="mb-3">
                            <Form.Label>Amount</Form.Label>
                            <Form.Control
                                type="text"
                                name="amount"        
                                value={formData.amount}
                                onChange={handleInputChange}
                                isInvalid={!!errors.amount}
                            />

                            <Form.Control.Feedback type="invalid">
                                {errors.amount}
                            </Form.Control.Feedback>
                        </Form.Group>
                        <Button type="submit" variant="primary">
                            Simpan
                        </Button>
                    </Form>
                </Modal.Body>
            </Modal>
        </div>
    );
}

export default JournalPage;
