import React, { useState, useEffect } from 'react';
import { Table } from 'react-bootstrap';
import moment from "moment";

function NeracaLajurPage() {    
    const [NeracaLajur, setNeracaLajur] = useState([]);
    const [loading, setLoading] = useState(true);


    useEffect(() => {
        setLoading(true);
        fetch(`http://localhost:7000/api/transactions/neraca-lajur`)
            .then((res) => res.json())
            .then((data) => {
                console.log(data.data);
                data.data.forEach(result => {
                    result.date = moment(new Date(result.date)).utc().format("DD/MM/YYYY");
                    var debit = result.debit;
                    var kredit = result.kredit;
                    const amountDebit = new Intl.NumberFormat("id-ID", {
                        style: "currency",
                        currency: "IDR",
                        maximumFractionDigits: 0,
                        }).format(debit);
                    const amountKredit = new Intl.NumberFormat("id-ID", {
                        style: "currency",
                        currency: "IDR",
                        maximumFractionDigits: 0,
                        }).format(kredit);        
                    result.debit = amountDebit.replace("Rp", "");
                    result.kredit = amountKredit.replace("Rp", "");
                }); 
                setNeracaLajur(data.data);
                setLoading(false);
            });
    },  []);

    return (
        <div>
            <div className="d-flex justify-content-between align-items-center mb-4">
            <h2>Neraca</h2>
            </div>
                {loading ? (
                        <div className="text-center">Loading...</div>
                    ) : (
                        <div>
                        <Table striped bordered hover>
                            <thead>
                                <tr>
                                    <th rowSpan={2}>Kode</th>
                                    <th rowSpan={2}>Nama Akun</th>
                                    <th colSpan={2}>Neraca Saldo</th>
                                    {/* <th colSpan={2}>Penyesuaian</th>
                                    <th colSpan={2}>Neraca Saldo <br/> Setelah Penyesuaian</th>
                                    <th colSpan={2}>Rugi Laba</th>
                                    <th colSpan={2}>Neraca</th> */}
                                </tr>
                                <tr>
                                    <th>Debit</th>
                                    <th>Credit</th>
                                    {/* <th>Debit</th>
                                    <th>Credit</th>
                                    <th>Debit</th>
                                    <th>Credit</th>
                                    <th>Debit</th>
                                    <th>Credit</th>
                                    <th>Debit</th>
                                    <th>Credit</th> */}
                                </tr>
                            </thead>
                            <tbody>
                            {NeracaLajur.map((neracaLajur, index) => (
                                <tr key={index}>
                                    <td>{neracaLajur.code}</td>
                                    {/* <td>{neracaLajur.neraca_saldo.name}</td>
                                    <td>{neracaLajur.neraca_saldo.debit}</td>
                                    <td>{neracaLajur.neraca_saldo.kredit}</td>
                                    <td>{neracaLajur.neraca_penyesuaian.debit}</td> */}

                                </tr>
                            ))}
                            </tbody>
                        </Table>
                        </div>     
                    )}
                </div>
    );
}

export default NeracaLajurPage;
