import React, { useState, useEffect } from 'react';
import { Table } from 'react-bootstrap';

function NeracaLajurPage() {    
    const [NeracaLajur, setNeracaLajur] = useState([]);
    const [loading, setLoading] = useState(true);


    useEffect(() => {
        setLoading(true);
        fetch(`http://localhost:7000/api/transactions/neraca-lajur`)
            .then((res) => res.json())
            .then((data) => {
                console.log(data.data[0].neracaSaldo.debit); 
                setNeracaLajur(data.data);
                setLoading(false);
            });
    },  []);

    return (
        <div>
            <div className="d-flex justify-content-between align-items-center mb-4">
            <h2>Neraca</h2>
            </div>
                {loading ? (
                        <div className="text-center">Loading...</div>
                    ) : (
                        <div>
                        <Table striped bordered hover>
                            <thead>
                                <tr>
                                    <th rowSpan={2}>Kode</th>
                                    <th rowSpan={2}>Nama Akun</th>
                                    <th colSpan={2}>Neraca Saldo</th>
                                    <th colSpan={2}>Penyesuaian</th>
                                    <th colSpan={2}>Neraca Saldo <br/> Setelah Penyesuaian</th>
                                    <th colSpan={2}>Rugi Laba</th>
                                    <th colSpan={2}>Neraca</th>
                                </tr>
                                <tr>
                                    <th>Debit</th>
                                    <th>Credit</th>
                                    <th>Debit</th>
                                    <th>Credit</th>
                                    <th>Debit</th>
                                    <th>Credit</th>
                                    <th>Debit</th>
                                    <th>Credit</th>
                                    <th>Debit</th>
                                    <th>Credit</th>
                                </tr>
                            </thead>
                            <tbody>
                            {NeracaLajur.map((neracaLajur, index) => (
                                <tr key={index}>
                                    <td>{neracaLajur.akun.code}</td>
                                    <td>{neracaLajur.akun.name}</td>
                                    <td>{neracaLajur.neracaSaldo.debit}</td>
                                    <td>{neracaLajur.neracaSaldo.kredit}</td>
                                </tr>
                            ))}
                            </tbody>
                        </Table>
                        </div>     
                    )}
                </div>
    );
}

export default NeracaLajurPage;
