// src/pages/JournalPage.js
import React, { useState, useEffect } from 'react';
import { Button, Table, Modal, Pagination } from 'react-bootstrap';
import moment from "moment";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import { FaCalendarAlt } from 'react-icons/fa';


function JournalPage() {
    const [journals, setJournals] = useState([]);
    const [loading, setLoading] = useState(true);
    const [showModal, setShowModal] = useState(false);
    const [formData, setFormData] = useState({ account_id: '', type: '', amount: '' , date: '' });
    const [errors, setErrors] = useState({});
    const [currentPage, setCurrentPage] = useState(1);
    const [totalPages, setTotalPages] = useState(1);
    const [isClicked, setIsCliked] = useState(false);
    const [startDate, setStartDate] = useState(new Date());

    const handleCalendarClose = () => console.log("Calendar closed");
    function CustomInput({value,onClick}){
        return(
            <div className='input-group'>
                <input className='form-control' 
                placeholder='dd/mm/yyyy' type='text' 
                value={value} 
                onClick={onClick} readOnly/>
                <div className='input-group-append'>
                    <span className='input-group-text'><FaCalendarAlt/></span>
                </div>
            </div>
        );
    }
    const fetchData = (page) => {
        setLoading(true);
        fetch(`http://localhost:7000/api/transactions?page=${page}`)
            .then((response) => response.json())
            .then((data) => {
                const formattedData = data.journals.map(journal => ({
                    ...journal,
                    date: new Date(journal.date).toLocaleDateString('id-ID') // Format dd/mm/yyyy
                }));
                setJournals(formattedData);
                setJournals(data.data);
                setTotalPages(data.totalPages);
                setLoading(false);
            })
            .catch((error) => {
                console.error('Error fetching data:', error);
                setLoading(false);
            });
    };

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        if (name === 'amount') {
            const formattedValue = value
                .replace(/\D/g, '') // Remove non-numeric characters
                .replace(/\B(?=(\d{3})+(?!\d))/g, '.'); // Add commas for thousands
            setFormData({ ...formData, [name]: formattedValue });
        } else {
            setFormData({ ...formData, [name]: value });
        }
    };

    const validateDate = (date) => {
        const dateRegex = /^\d{4}-\d{2}-\d{2}$/;
        if(date === 'Invalid date') return 'date not be empty';
        if (!dateRegex.test(date)) return 'Format date must be YYYY-MM-DD';
        const inputDate = moment(new Date(date)).format("YYYY-MM-DD");
        const currentDate = moment(new Date()).format("YYYY-MM-DD");
        if (inputDate !== currentDate) return "The date is not the same as today's date";
        return null;
    };

    const validateForm = () => {
        const newErrors = {};

        if(formData.account_id !== ''){
            if (!formData.account_id || !/^[0-9]+$/.test(formData.account_id)) {
                newErrors.account_id = 'Akun must be a valid number.';
            }
        }else{
            newErrors.account_id = 'Akun is required.';
        }

        if(formData.type !== ''){
            if (!formData.type || !/^[a-zA-Z]+$/.test(formData.type)) {
                newErrors.type = 'Type must be a valid character.';
            }
        }else{
            newErrors.type = 'Type is required.';
        }

        if(formData.amount !== ''){
            const amountTemp= formData.amount.replaceAll(".","");
            if (!amountTemp || !/^[0-9]+$/.test(amountTemp)) {
                newErrors.amount = 'amount must be a valid number.';
            }
        }else{
            newErrors.amount = 'amount is required.';
        }

        // if (!formData.account_id.match(/^[0-9]+$/)) newErrors.description = 'Akun must be a valid number.';
        // if (!formData.type.match(/^[a-zA-Z ]+$/)) newErrors.debit = 'Type must be a valid character.';
        // if (!formData.amount.match(/^[0-9 ]+$/)) newErrors.credit = 'amount must be a valid number.';
        formData.date = moment(startDate).format("YYYY-MM-DD");
        const dateError = validateDate(formData.date);
        if (dateError) newErrors.date = dateError;
        setErrors(newErrors);
        return Object.keys(newErrors).length === 0;
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        if (!validateForm()) return;

        // fetch('http://localhost:7000/api/transactions/send', {
        //     method: 'POST',
        //     headers: { 'Content-Type': 'application/json' },
        //     body: JSON.stringify(formData),
        // })
        //     .then((response) => response.json())
        //     .then((data) => {
        //         setFormData({ account_id: '', type: '', amount: '', date: '' });
        //         setShowModal(false);
        //         setIsCliked(bool => !bool);
        //     })
        //     .catch((error) => console.error('Error submitting data:', error));
    };

    const handlePageChange = (page) => {
        setCurrentPage(page);
    };



    useEffect(() => {
        fetchData(currentPage);
    }, [currentPage,isClicked]);
    return (
        <div>
            <div className="d-flex justify-content-between align-items-center mb-4">
                <h2>Jurnal Umum</h2>
                <Button className="mb-3" onClick={() => setShowModal(true)}>
                    Tambah Jurnal
                </Button>
            </div>
            <div className="card">
                <div className="card-header">Data Jurnal Umum</div>
                <div className="card-body">
                    {loading ? (
                        <div>Loading...</div>
                    ) : (
                        <Table striped bordered hover>
                            <thead>
                                <tr>
                                    <th>No</th>
                                    <th>Tanggal</th>
                                    <th>Akun</th>
                                    <th>Debit</th>
                                    <th>Kredit</th>

                                </tr>
                            </thead>
                            <tbody>
