import React, { useState, useEffect } from 'react';
import { Button, Table, Form, Modal, Pagination } from 'react-bootstrap';


function AkunPage() {
    const [akuns, setakuns] = useState([]);
    const [showModal, setShowModal] = useState(false);
    const [loading, setLoading] = useState(true);
    const [currentPage, setCurrentPage] = useState(1);
    const [totalPages, setTotalPages] = useState(1);
    const [formData, setFormData] = useState({
        code: '',
        name: '',
        type: '',
    });
    const [errors, setErrors] = useState({});
    const [isClicked, setIsCliked] = useState(false);
    //const [showForm, setShowForm] = useState(false);

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });
    };
    const validateForm = () => {
        const newErrors = {};
        if (!formData.code || !/^[0-9]+$/.test(formData.code)) {
            newErrors.code = 'Akun must be a valid number.';
        }
        if (!formData.name || !/^[\w ]+$/.test(formData.name)) {
            newErrors.name = 'Nama must be a valid character.';
        }
        console.log(formData.type);
        if (!formData.type || !/^[a-zA-Z]+$/.test(formData.type)) {
            newErrors.type = 'Type must be a valid character.';
        }
        setErrors(newErrors);
        return Object.keys(newErrors).length === 0;
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        if (!validateForm()) return;

        fetch('http://localhost:7000/api/accounts/send', {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify(formData),
        })
            .then((response) => response.json())
            .then((data) => {
                setFormData({ code: '', name: '', type: '' });
                setShowModal(false);
                setIsCliked(bool => !bool);
            })
            .catch((error) => console.error('Error adding akun:', error));
            // try {
            //     const response = await axios.post('http://localhost:7000/api/accounts/send', formData);
            //     setJournals([...journals, response.data]);
            //     setFormData({ code: '', name: '', type: '' });
            //    // setShowForm(false);
            // } catch (error) {
            //     console.error('Error adding journal:', error);
            // }

    };

    useEffect(() => {
        setLoading(true);
        fetch(`http://localhost:7000/api/accounts/list?page=${currentPage}`)
            .then((res) => res.json())
            .then((data) => {
                setakuns(data.data);
                setTotalPages(data.totalPages);
                setLoading(false);
            });
    }, [currentPage,isClicked]);

    const handlePageChange = (page) => {
        setCurrentPage(page);
    };

    return (
        <div>
            <div className="d-flex justify-content-between align-items-center mb-4">
                <h2>Data Akun</h2>
                 <Button className="mb-3" onClick={() => setShowModal(true)}>
                    Tambah Akun
                 </Button>
            </div>     
            <div className="card">
                <div className="card-header">Data Akun</div>
                <div className="card-body">
                {loading ? (
                        <div className="text-center">Loading...</div>
                    ) : (
                    <Table striped bordered hover>
                        <thead>
                            <tr>
                                <th>No</th>
                                <th>Akun</th>
                                <th>Nama</th>
                                <th>Type</th>
                            </tr>
                        </thead>
                        <tbody>
                            {akuns.map((akun,index) => (
                                <tr key={index}>
                                    <td>{index + 1}</td>
                                    <td>{akun.code}</td>
                                    <td>{akun.name}</td>
                                    <td>{akun.type}</td>
                                </tr>
                            ))}
                        </tbody>
                    </Table>
                    )}
                    <Pagination>
                        {[...Array(totalPages).keys()].map((page) => (
                            <Pagination.Item
                                key={page + 1}
                                active={page + 1 === currentPage}
                                onClick={() => handlePageChange(page + 1)}
                            >
                                {page + 1}
                            </Pagination.Item>
                        ))}
                    </Pagination>
                </div>
            </div>
             <Modal show={showModal} onHide={() => setShowModal(false)}>
                <Modal.Header closeButton>
                    <Modal.Title>Tambah Jurnal</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                <form onSubmit={handleSubmit}>
                            <div className="mb-3">
                                <label className="form-label">Akun</label>
                                <input
                                    type="text"
                                    className={`form-control ${errors.code ? 'is-invalid' : ''}`}
                                    name="code"
                                    value={formData.code}
                                    isInvalid={!!errors.code} 
                                    onChange={handleInputChange}/>
                                {errors.code && <div className="invalid-feedback">{errors.code}</div>}
                            </div>
                            <div className="mb-3">
                                <label className="form-label">Nama</label>
                                <input
                                    type="text"
                                    className={`form-control ${errors.name ? 'is-invalid' : ''}`}
                                    name="name"
                                    isInvalid={!!errors.name} 
                                    value={formData.name}
                                    onChange={handleInputChange}/>
                                {errors.name && <div className="invalid-feedback">{errors.name}</div>}
                            </div>
                            <div className="mb-3">
                                <label className="form-label">Type</label>
                                <select
                                    className={`form-select ${errors.type ? 'is-invalid' : ''}`}
                                    name="type"
                                    isInvalid={!!errors.type} 
                                    value={formData.type}
                                    onChange={handleInputChange}>
                                        <option selected>Pilih Type</option>
                                        <option value="Asset">Asset</option>
                                        <option value="Liability">Liability</option>
                                        <option value="Equity">Equity</option>
                                        <option value="Revenue">Revenue</option>
                                        <option value="Expense">Expense</option>
                                </select>        
                                {errors.type && <div className="invalid-feedback">{errors.type}</div>}
                            </div>
                            <button type="submit" className="btn btn-success">
                                Simpan
                            </button>
                        </form>
                    {/* <Form onSubmit={handleSubmit}>
                        <Form.Group className="mb-3">
                            <Form.Label>Akun</Form.Label>
