                                <option value="Revenue">Revenue</option>
                                <option value="Expense">Expense</option>
                            </Form.Select>
                            <Form.Control.Feedback type="invalid">
                                {errors.type}
                            </Form.Control.Feedback>
                        </Form.Group>
                        <Button type="submit" variant="primary">
                            Simpan
                        </Button>
                    </Form> */}
                </Modal.Body>
            </Modal>
        </div>
    );
}

export default AkunPage;
